css:
.landing-page {
    padding:15px 0;
}
.landing-page h1 {
  color: #2A2E43;
  font-weight:600;
  font-size: 2em;
  letter-spacing: 1px;
  line-height: 2em;
  margin: 0;
  text-align: center;
  width: 100%;
    text-align:center;
}
.inner-hiring a {
    text-decoration:none;
}
.header-titlle {
    font-size:16px;
    color:#78849E;
    text-align:center;
    margin-bottom: 3rem;
}
.landing-page .hiring {
    background:#e8f8fc;
     border: 1px solid#79D5EE;
    border-radius: var(--border-radius-md);
}
.landing-page .hiring h3 {
    font-size: 28px;
    color: #2A2E43;
    padding-top: 1rem;
}
.landing-page .hiring button {
    font-size: 16px;
    margin-top: 2rem;
}
.landing-page .hiring p {
    font-size:16px;
    color:#454F63;
}
.landing-page .hiring .inner-hiring {
    padding: 1rem 3rem 1rem 2rem;;
}
.feature-hiring {
    padding: 30px 50px;
}
.feature-hiring h3 {
    font-size: 24px;
    font-weight: normal;
}
.feature-hiring h3 span {
    color:#21B9E4;
}
.feature-hiring .img {
    border-radius: 50px;
    margin: 10px;
    padding: 16px;
}
.feature-hiring .img.blue {
     background: #F1EFFD;
}
.feature-hiring .img.green {
     background: #E8FCF6;
}
.feature-hiring .img.yellow {
     background: #FFF8E4;
}
.feature-hiring .img.red {
     background: #FEF0F0;
}
.feature-hiring p {
    font-size: 16px;
    padding: 8px 80px 0 0;
}
.landing-page .hiring.staffing {
    background: #F1EFFD;
    border: 1px solid#D7D1F9;
}
.landing-page .hiring.staffing button {
    background:#7B68EE !important;
}
.widget {
    display: flex;
    flex-direction: column;
    min-height: 1px;
    border-radius: var(--border-radius-md);
    height: 100%;
    box-shadow: var(--card-shadow);
    background-color: var(--card-bg);
}
#temporary-assistance-guru {
        background: #FAFBFC;
}
.footer small, .footer a {
    font-size:13px;
}
.footer a {
    color:#21B9E4;
}
