css:
.print-main-section .right-align {
    text-align:right;
    padding-top:10px;
}
.print-main-section table {
    width:100%;
}
.print-main-section .logo{
    max-width: max-content;
}
.pt-1 {
    padding-top:10px;
}
p {
    font-size:14px;
}
.due-payment {
    width:55%;
    padding: 5px 15px;
    margin-top: 20px;
    border: 1px solid #dee2e6;
    text-align: left;
    float: right;
}

.due-payment p {
        font-size: 16px;
    font-weight: 500;
}
.due-payment label {
    font-weight: 400;
    margin: 10px 0 5px 0;
    text-align: left;
}
.description h5 {
    font-weight:700 !important;
}
.job-description {
   margin:30px 0;
   font-size:14px;
}
.job-description label {
        font-weight: normal;
        margin: 7px 0;
        color: #6c757d;
}
.job-description p {
    font-weight:500;
}
.address label {
    width:65%;
}
.footer-text{
    text-align: right;
    font-size: 16px;
    font-weight: 500;
}
.payment-list {
    padding: 5px 15px;
    margin-top: 30px;
    border: 1px solid #dee2e6;
    text-align: left;
    width: 60%;
}
.payment-list p {
    font-size: 16px;
    font-weight: 500;
}
.payment-list label {
    margin: 8px 0 0 0;
    font-weight: 400;
}
.footer-date label {
    font-weight:normal;
    font-size:14px;
}
.footer-date label span {
    color: #8D9CBC;
    padding-right: 5px;
}
.footer-date{
    padding:30px 0;
}
.right {
    float:right;
}
.sub-list {
    background: #ECECF1;
   padding: 10px 15px;
}
.sub-list label {
    display: inline-block;
    font-weight: 400;
    font-size:14px;
}
.pt-2 {
    padding-top:22px;
}
.address label {
    width: 85%;
}
.print-format td {
   width: 25%;
}
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    font-weight: 500 !important;
    padding: 12px !important;
    text-align: left;
}
.right-section {
	padding-left: 90px;
}
.comments {
    text-align: justify;
}
.custom-comment {
    height: 75px;
    overflow: hidden;
    color: #6c757d!important;
    font-size:13px!important;
}
